    const [Items, setItems] = useState([])

    const show = (e) => {
        setList(e.target.value)
    }

    const addList = (e) => {
        if (List === "") {
            return alert(`please input a data`)
        } else {
            setItems(() => {
                return [...Items, List]
            })
            setList('')
        }
    }
