import React, { useState } from "react";

const TODO = () => {

    const [List, setList] = useState()
    const [Items, setItems] = useState([])

    const show = (e) => {
        setList(e.target.value)
    }

    const addList = (e) => {
      setItems((pre) => {
        return [...Items, List]
      })
      setList('')
    }

    return (
        <>
            <div className='main_div'>
                <div className='todo'>
                    <br />
                    <h1 className='heading'>Welcome To my ToDo list</h1>
                    <br />
                    <input type='text' name='item' onChange={show} value={List} placeholder='Add Your List' className='srh' autoComplete="off" />
                    <button className='btn' onClick={addList}> + </button>
                    <ol>
                        {Items.map((curr) => {
                            return <li >{curr}</li> 
                        })}
                </ol>
            </div>
        </div >
        </>
    )
}

export default TODO
